import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import dotenv from "dotenv";
dotenv.config();

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Logging middleware
app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  // Error handling middleware
  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";
    res.status(status).json({ message });
    throw err;
  });

  // Setup Vite only in development
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // Use the PORT from environment variables, default to 5000
  const port = parseInt(process.env.PORT || "5000", 10);

  // IMPORTANT: Use 0.0.0.0 for cloud deployments
  server.listen(port, "0.0.0.0", () => {
    log(`Serving on http://0.0.0.0:${port}`);
  });
})();

server/routes.ts.content:
import { response, type Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  loginSchema, 
  insertEmployeeSchema, 
  insertAttendanceSchema, 
  insertLeaveRequestSchema,
  updateLeaveStatusSchema
} from "@shared/schema";
import { 
  generateToken, 
  hashPassword, 
  comparePassword, 
  authenticateToken, 
  requireRole,
  type AuthRequest 
} from "./middleware/auth";

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication routes
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = loginSchema.parse(req.body);

      const employee = await storage.getEmployeeByEmail(email);
      if (!employee) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValidPassword = await comparePassword(password, employee.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const token = generateToken(employee);
      const { password: _, ...employeeData } = employee;

      res.json({ 
        token, 
        employee: employeeData 
      });
    } catch (error) {
      res.status(400).json({ message: "Invalid request data" });
    }
  });

  app.get("/api/auth/me", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const employee = await storage.getEmployee(req.user!.id);
      if (!employee) {
        return res.status(404).json({ message: "Employee not found" });
      }

      const { password: _, ...employeeData } = employee;
      res.json(employeeData);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Employee routes
  app.get("/api/employees", authenticateToken, requireRole(["admin"]), async (req, res) => {
    try {
      const employees = await storage.getAllEmployees();
      const employeesWithoutPasswords = employees.map(({ password, ...emp }) => emp);
      res.json(employeesWithoutPasswords);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/employees", authenticateToken, requireRole(["admin"]), async (req, res) => {

    try {
      const employeeData = insertEmployeeSchema.parse(req.body);
      const hashedPassword = await hashPassword(employeeData.password);

      const employee = await storage.createEmployee({
        ...employeeData,
        password: hashedPassword,
      });

      const { password: _, ...employeeResponse } = employee;
      res.status(201).json(employeeResponse);
    } catch (error) {
      res.status(400).json({ message: "Invalid employee data" });
    }
  });

  app.put("/api/employees/:id", authenticateToken, requireRole(["admin"]), async (req, res) => {
    try {
      const { id } = req.params;
      const updateData = req.body;

      if (updateData.password) {
        updateData.password = await hashPassword(updateData.password);
      }

      const employee = await storage.updateEmployee(id, updateData);
      if (!employee) {
        return res.status(404).json({ message: "Employee not found" });
      }

      const { password: _, ...employeeResponse } = employee;
      res.json(employeeResponse);
    } catch (error) {
      res.status(400).json({ message: "Invalid update data" });
    }
  });

  app.delete("/api/employees/:id", authenticateToken, requireRole(["admin"]), async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteEmployee(id);

      if (!deleted) {
        return res.status(404).json({ message: "Employee not found" });
      }

      res.json({ message: "Employee deleted successfully" });
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Attendance routes
  app.post("/api/attendance/checkin", authenticateToken, async (req: AuthRequest, res) => {
    console.log("Hello", authenticateToken)
    try {
      const employeeId = req.user!.id;
      const today = new Date();

      // Check if already checked in today
      const existingAttendance = await storage.getEmployeeAttendance(employeeId, today);
      if (existingAttendance) {
        return res.status(400).json({ message: "Already checked in today" });
      }

      const attendance = await storage.createAttendance({
        employeeId,
        date: today,
        checkInTime: today,
        status: "present",
      });

      res.status(201).json(attendance);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/attendance/checkout", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const employeeId = req.user!.id;
      const today = new Date();

      const existingAttendance = await storage.getEmployeeAttendance(employeeId, today);
      if (!existingAttendance) {
        return res.status(400).json({ message: "No check-in found for today" });
      }

      if (existingAttendance.checkOutTime) {
        return res.status(400).json({ message: "Already checked out today" });
      }

      const checkOutTime = new Date();
      const hoursWorked = existingAttendance.checkInTime 
        ? Math.round((checkOutTime.getTime() - existingAttendance.checkInTime.getTime()) / (1000 * 60 * 60) * 10) / 10
        : 0;

      const updatedAttendance = await storage.updateAttendance(existingAttendance.id, {
        checkOutTime,
        hoursWorked,
      });

      res.json(updatedAttendance);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/attendance/today", authenticateToken, requireRole(["admin"]), async (req, res) => {
    try {
      const todayAttendance = await storage.getTodayAttendance();
      res.json(todayAttendance);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/attendance/my", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const employeeId = req.user!.id;
      const today = new Date();
      const attendance = await storage.getEmployeeAttendance(employeeId, today);
      res.json(attendance || null);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Leave request routes
  app.post("/api/leave-requests", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const leaveData = insertLeaveRequestSchema.parse({
        ...req.body,
        employeeId: req.user!.id,
      });

      // Calculate days requested
      const startDate = new Date(leaveData.startDate);
      const endDate = new Date(leaveData.endDate);
      const daysRequested = Math.ceil((endDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24)) + 1;

      const leaveRequest = await storage.createLeaveRequest({
        ...leaveData,
        daysRequested,
      });

      res.status(201).json(leaveRequest);
    } catch (error) {
      res.status(400).json({ message: "Invalid leave request data" });
    }
  });

  app.get("/api/leave-requests/my", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const employeeId = req.user!.id;
      const leaveRequests = await storage.getEmployeeLeaveRequests(employeeId);
      res.json(leaveRequests);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/leave-requests/pending", authenticateToken, requireRole(["admin"]), async (req, res) => {
    try {
      const pendingRequests = await storage.getPendingLeaveRequests();
      res.json(pendingRequests);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.put("/api/leave-requests/:id/status", authenticateToken, requireRole(["admin"]), async (req: AuthRequest, res) => {
    try {
      const { id } = req.params;
      const statusUpdate = updateLeaveStatusSchema.parse(req.body);

      const updatedRequest = await storage.updateLeaveRequestStatus(id, {
        ...statusUpdate,
        reviewedBy: req.user!.id,
      });

      if (!updatedRequest) {
        return res.status(404).json({ message: "Leave request not found" });
      }

      res.json(updatedRequest);
    } catch (error) {
      res.status(400).json({ message: "Invalid status update data" });
    }
  });

  // Dashboard routes
  app.get("/api/dashboard/stats", authenticateToken, requireRole(["admin"]), async (req, res) => {
    try {
      const stats = await storage.getDashboardStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  employees, 
  attendance, 
  leaveRequests,
  type Employee, 
  type InsertEmployee,
  type Attendance,
  type InsertAttendance,
  type LeaveRequest,
  type InsertLeaveRequest,
  type UpdateLeaveStatus
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, sql, gte, lte, count } from "drizzle-orm";

export interface IStorage {
  // Employee operations
  getEmployee(id: string): Promise<Employee | undefined>;
  getEmployeeByEmail(email: string): Promise<Employee | undefined>;
  createEmployee(employee: InsertEmployee): Promise<Employee>;
  updateEmployee(id: string, employee: Partial<InsertEmployee>): Promise<Employee | undefined>;
  deleteEmployee(id: string): Promise<boolean>;
  getAllEmployees(): Promise<Employee[]>;

  // Attendance operations
  createAttendance(attendance: InsertAttendance): Promise<Attendance>;
  getEmployeeAttendance(employeeId: string, date?: Date): Promise<Attendance | undefined>;
  getTodayAttendance(): Promise<(Attendance & { employee: Employee })[]>;
  updateAttendance(id: string, updates: Partial<Attendance>): Promise<Attendance | undefined>;

  // Leave request operations
  createLeaveRequest(leaveRequest: InsertLeaveRequest): Promise<LeaveRequest>;
  getEmployeeLeaveRequests(employeeId: string): Promise<LeaveRequest[]>;
  getPendingLeaveRequests(): Promise<(LeaveRequest & { employee: Employee })[]>;
  updateLeaveRequestStatus(id: string, updates: UpdateLeaveStatus & { reviewedBy: string }): Promise<LeaveRequest | undefined>;

  // Dashboard statistics
  getDashboardStats(): Promise<{
    totalEmployees: number;
    presentToday: number;
    pendingLeaves: number;
    departments: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  async getEmployee(id: string): Promise<Employee | undefined> {
    const [employee] = await db.select().from(employees).where(eq(employees.id, id));
    return employee || undefined;
  }

  async getEmployeeByEmail(email: string): Promise<Employee | undefined> {
    const [employee] = await db.select().from(employees).where(eq(employees.email, email));
    return employee || undefined;
  }

  async createEmployee(employee: InsertEmployee): Promise<Employee> {
    const [newEmployee] = await db
      .insert(employees)
      .values({
        ...employee,
        updatedAt: new Date(),
      })
      .returning();
    return newEmployee;
  }

  async updateEmployee(id: string, employee: Partial<InsertEmployee>): Promise<Employee | undefined> {
    const [updatedEmployee] = await db
      .update(employees)
      .set({
        ...employee,
        updatedAt: new Date(),
      })
      .where(eq(employees.id, id))
      .returning();
    return updatedEmployee || undefined;
  }

  async deleteEmployee(id: string): Promise<boolean> {
    const result = await db.delete(employees).where(eq(employees.id, id));
    return result.rowCount > 0;
  }

  async getAllEmployees(): Promise<Employee[]> {
    return await db.select().from(employees).where(eq(employees.isActive, true));
  }

  async createAttendance(attendanceData: InsertAttendance): Promise<Attendance> {
    const [newAttendance] = await db
      .insert(attendance)
      .values(attendanceData)
      .returning();
    return newAttendance;
  }

  async getEmployeeAttendance(employeeId: string, date?: Date): Promise<Attendance | undefined> {
    const targetDate = date || new Date();
    const startOfDay = new Date(targetDate);
    startOfDay.setHours(0, 0, 0, 0);
    const endOfDay = new Date(targetDate);
    endOfDay.setHours(23, 59, 59, 999);

    const [attendanceRecord] = await db
      .select()
      .from(attendance)
      .where(
        and(
          eq(attendance.employeeId, employeeId),
          gte(attendance.date, startOfDay),
          lte(attendance.date, endOfDay)
        )
      );
    return attendanceRecord || undefined;
  }

  async getTodayAttendance(): Promise<(Attendance & { employee: Employee })[]> {
    const today = new Date();
    const startOfDay = new Date(today);
    startOfDay.setHours(0, 0, 0, 0);
    const endOfDay = new Date(today);
    endOfDay.setHours(23, 59, 59, 999);

    return await db
      .select({
        id: attendance.id,
        employeeId: attendance.employeeId,
        date: attendance.date,
        checkInTime: attendance.checkInTime,
        checkOutTime: attendance.checkOutTime,
        hoursWorked: attendance.hoursWorked,
        status: attendance.status,
        createdAt: attendance.createdAt,
        employee: employees,
      })
      .from(attendance)
      .innerJoin(employees, eq(attendance.employeeId, employees.id))
      .where(
        and(
          gte(attendance.date, startOfDay),
          lte(attendance.date, endOfDay)
        )
      );
  }

  async updateAttendance(id: string, updates: Partial<Attendance>): Promise<Attendance | undefined> {
    const [updatedAttendance] = await db
      .update(attendance)
      .set(updates)
      .where(eq(attendance.id, id))
      .returning();
    return updatedAttendance || undefined;
  }

  async createLeaveRequest(leaveRequest: InsertLeaveRequest): Promise<LeaveRequest> {
    const [newLeaveRequest] = await db
      .insert(leaveRequests)
      .values({
        ...leaveRequest,
        updatedAt: new Date(),
      })
      .returning();
    return newLeaveRequest;
  }

  async getEmployeeLeaveRequests(employeeId: string): Promise<LeaveRequest[]> {
    return await db
      .select()
      .from(leaveRequests)
      .where(eq(leaveRequests.employeeId, employeeId))
      .orderBy(desc(leaveRequests.createdAt));
  }

  async getPendingLeaveRequests(): Promise<(LeaveRequest & { employee: Employee })[]> {
    return await db
      .select({
        id: leaveRequests.id,
        employeeId: leaveRequests.employeeId,
        leaveType: leaveRequests.leaveType,
        startDate: leaveRequests.startDate,
        endDate: leaveRequests.endDate,
        reason: leaveRequests.reason,
        status: leaveRequests.status,
        reviewedBy: leaveRequests.reviewedBy,
        reviewedAt: leaveRequests.reviewedAt,
        reviewComments: leaveRequests.reviewComments,
        daysRequested: leaveRequests.daysRequested,
        createdAt: leaveRequests.createdAt,
        updatedAt: leaveRequests.updatedAt,
        employee: employees,
      })
      .from(leaveRequests)
      .innerJoin(employees, eq(leaveRequests.employeeId, employees.id))
      .where(eq(leaveRequests.status, "pending"))
      .orderBy(desc(leaveRequests.createdAt));
  }

  async updateLeaveRequestStatus(id: string, updates: UpdateLeaveStatus & { reviewedBy: string }): Promise<LeaveRequest | undefined> {
    const [updatedLeaveRequest] = await db
      .update(leaveRequests)
      .set({
        ...updates,
        reviewedAt: new Date(),
        updatedAt: new Date(),
      })
      .where(eq(leaveRequests.id, id))
      .returning();
    return updatedLeaveRequest || undefined;
  }

  async getDashboardStats(): Promise<{
    totalEmployees: number;
    presentToday: number;
    pendingLeaves: number;
    departments: number;
  }> {
    const today = new Date();
    const startOfDay = new Date(today);
    startOfDay.setHours(0, 0, 0, 0);
    const endOfDay = new Date(today);
    endOfDay.setHours(23, 59, 59, 999);

    const [totalEmployeesResult] = await db
      .select({ count: count() })
      .from(employees)
      .where(eq(employees.isActive, true));

    const [presentTodayResult] = await db
      .select({ count: count() })
      .from(attendance)
      .where(
        and(
          gte(attendance.date, startOfDay),
          lte(attendance.date, endOfDay),
          eq(attendance.status, "present")
        )
      );

    const [pendingLeavesResult] = await db
      .select({ count: count() })
      .from(leaveRequests)
      .where(eq(leaveRequests.status, "pending"));

    const departmentsResult = await db
      .selectDistinct({ department: employees.department })
      .from(employees)
      .where(eq(employees.isActive, true));

    return {
      totalEmployees: totalEmployeesResult.count,
      presentToday: presentTodayResult.count,
      pendingLeaves: pendingLeavesResult.count,
      departments: departmentsResult.length,
    };
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql, relations } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, integer, boolean, pgEnum } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const roleEnum = pgEnum("role", ["employee", "admin"]);
export const leaveStatusEnum = pgEnum("leave_status", ["pending", "approved", "rejected"]);
export const leaveTypeEnum = pgEnum("leave_type", ["sick", "vacation", "personal", "emergency"]);

export const employees = pgTable("employees", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  firstName: text("first_name").notNull(),
  lastName: text("last_name").notNull(),
  department: text("department").notNull(),
  position: text("position").notNull(),
  role: roleEnum("role").default("employee").notNull(),
  leaveBalance: integer("leave_balance").default(20).notNull(),
  isActive: boolean("is_active").default(true).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const attendance = pgTable("attendance", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  employeeId: varchar("employee_id").references(() => employees.id, { onDelete: "cascade" }).notNull(),
  date: timestamp("date").defaultNow().notNull(),
  checkInTime: timestamp("check_in_time"),
  checkOutTime: timestamp("check_out_time"),
  hoursWorked: integer("hours_worked").default(0),
  status: text("status").default("present").notNull(), // present, absent, on_break
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const leaveRequests = pgTable("leave_requests", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  employeeId: varchar("employee_id").references(() => employees.id, { onDelete: "cascade" }).notNull(),
  leaveType: leaveTypeEnum("leave_type").notNull(),
  startDate: timestamp("start_date").notNull(),
  endDate: timestamp("end_date").notNull(),
  reason: text("reason"),
  status: leaveStatusEnum("status").default("pending").notNull(),
  reviewedBy: varchar("reviewed_by").references(() => employees.id),
  reviewedAt: timestamp("reviewed_at"),
  reviewComments: text("review_comments"),
  daysRequested: integer("days_requested").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Relations
export const employeesRelations = relations(employees, ({ many, one }) => ({
  attendance: many(attendance),
  leaveRequests: many(leaveRequests),
  reviewedLeaves: many(leaveRequests, { relationName: "reviewer" }),
}));

export const attendanceRelations = relations(attendance, ({ one }) => ({
  employee: one(employees, {
    fields: [attendance.employeeId],
    references: [employees.id],
  }),
}));

export const leaveRequestsRelations = relations(leaveRequests, ({ one }) => ({
  employee: one(employees, {
    fields: [leaveRequests.employeeId],
    references: [employees.id],
  }),
  reviewer: one(employees, {
    fields: [leaveRequests.reviewedBy],
    references: [employees.id],
    relationName: "reviewer",
  }),
}));

// Insert schemas
export const insertEmployeeSchema = createInsertSchema(employees).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertAttendanceSchema = createInsertSchema(attendance).omit({
  id: true,
  createdAt: true,
});

export const insertLeaveRequestSchema = createInsertSchema(leaveRequests).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  reviewedBy: true,
  reviewedAt: true,
  reviewComments: true,
});

export const loginSchema = z.object({
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

export const updateLeaveStatusSchema = z.object({
  status: z.enum(["approved", "rejected"]),
  reviewComments: z.string().optional(),
});

// Types
export type Employee = typeof employees.$inferSelect;
export type InsertEmployee = z.infer<typeof insertEmployeeSchema>;
export type Attendance = typeof attendance.$inferSelect;
export type InsertAttendance = z.infer<typeof insertAttendanceSchema>;
export type LeaveRequest = typeof leaveRequests.$inferSelect;
export type InsertLeaveRequest = z.infer<typeof insertLeaveRequestSchema>;
export type LoginCredentials = z.infer<typeof loginSchema>;
export type UpdateLeaveStatus = z.infer<typeof updateLeaveStatusSchema>;

