}

client/src/pages/employee-leave-requests.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { format } from "date-fns";
import { apiRequest } from "@/lib/queryClient";
import { getAuthHeaders } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import Sidebar from "@/components/sidebar";
import LeaveRequestForm from "@/components/leave-request-form";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Plus, Calendar, Clock, CheckCircle, XCircle, Hourglass } from "lucide-react";
import type { LeaveRequest } from "@shared/schema";

export default function EmployeeLeaveRequests() {
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  // Fetch user's leave requests
  const { data: leaveRequests = [], isLoading } = useQuery<LeaveRequest[]>({
    queryKey: ["/api/leave-requests/my"],
    queryFn: async () => {
      const response = await fetch("/api/leave-requests/my", {
        headers: getAuthHeaders(),
      });
      if (!response.ok) throw new Error("Failed to fetch leave requests");
      return await response.json();
    },
  });

  // Submit leave request mutation
  const submitLeaveRequestMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/leave-requests", data);
      return await response.json();
    },
    onSuccess: () => {
      toast({ 
        title: "Success", 
        description: "Leave request submitted successfully" 
      });
      setIsDialogOpen(false);
      queryClient.invalidateQueries({ queryKey: ["/api/leave-requests/my"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to submit leave request",
        variant: "destructive",
      });
    },
  });

  const handleSubmitLeaveRequest = (data: any) => {
    submitLeaveRequestMutation.mutate(data);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "pending":
        return (
          <Badge className="bg-yellow-100 text-yellow-800 hover:bg-yellow-100">
            <Hourglass className="h-3 w-3 mr-1" />
            Pending
          </Badge>
        );
      case "approved":
        return (
          <Badge className="bg-green-100 text-green-800 hover:bg-green-100">
            <CheckCircle className="h-3 w-3 mr-1" />
            Approved
          </Badge>
        );
      case "rejected":
        return (
          <Badge className="bg-red-100 text-red-800 hover:bg-red-100">
            <XCircle className="h-3 w-3 mr-1" />
            Rejected
          </Badge>
        );
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const getLeaveTypeBadge = (type: string) => {
    const colors = {
      sick: "bg-red-50 text-red-700 border-red-200",
      vacation: "bg-blue-50 text-blue-700 border-blue-200",
      personal: "bg-purple-50 text-purple-700 border-purple-200",
      emergency: "bg-orange-50 text-orange-700 border-orange-200",
    };

    return (
      <Badge variant="outline" className={colors[type as keyof typeof colors] || "bg-gray-50 text-gray-700 border-gray-200"}>
        {type.charAt(0).toUpperCase() + type.slice(1)}
      </Badge>
    );
  };

  // Calculate stats
  const stats = {
    total: leaveRequests.length,
    pending: leaveRequests.filter(req => req.status === "pending").length,
    approved: leaveRequests.filter(req => req.status === "approved").length,
    rejected: leaveRequests.filter(req => req.status === "rejected").length,
    balance: user?.leaveBalance || 0,
  };

  if (isLoading) {
    return (
      <div className="flex h-screen bg-background">
        <Sidebar />
        <div className="flex-1 flex items-center justify-center">
          <div className="text-muted-foreground">Loading leave requests...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-background">
      <Sidebar />

      <div className="flex-1 overflow-hidden">
        <div className="relative z-0 flex-1 overflow-y-auto focus:outline-none">
          <div className="py-6">
            <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
              {/* Header */}
              <div className="mb-8">
                <div className="flex items-center justify-between">
                  <div>
                    <h1 className="text-2xl font-bold">Leave Requests</h1>
                    <p className="text-muted-foreground">Submit and track your leave requests.</p>
                  </div>
                  <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                    <DialogTrigger asChild>
                      <Button data-testid="button-new-leave-request">
                        <Plus className="h-4 w-4 mr-2" />
                        New Request
                      </Button>
                    </DialogTrigger>
                    <DialogContent className="max-w-md">
                      <DialogHeader>
                        <DialogTitle>Submit Leave Request</DialogTitle>
                      </DialogHeader>
                      <LeaveRequestForm
                        onSubmit={handleSubmitLeaveRequest}
                        onCancel={() => setIsDialogOpen(false)}
                        isLoading={submitLeaveRequestMutation.isPending}
                      />
                    </DialogContent>
                  </Dialog>
                </div>
              </div>

              {/* Stats Cards */}
              <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 gap-6 mb-8">
                <Card>
                  <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                    <CardTitle className="text-sm font-medium">Leave Balance</CardTitle>
                    <Calendar className="h-4 w-4 text-green-600" />
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold text-green-600" data-testid="stat-leave-balance">
                      {stats.balance}
                    </div>
                    <p className="text-xs text-muted-foreground">days remaining</p>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                    <CardTitle className="text-sm font-medium">Total Requests</CardTitle>
                    <Calendar className="h-4 w-4 text-muted-foreground" />
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold" data-testid="stat-total-requests">
                      {stats.total}
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                    <CardTitle className="text-sm font-medium">Pending</CardTitle>
                    <Clock className="h-4 w-4 text-yellow-600" />
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold text-yellow-600" data-testid="stat-pending-requests">
                      {stats.pending}
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                    <CardTitle className="text-sm font-medium">Approved</CardTitle>
                    <CheckCircle className="h-4 w-4 text-green-600" />
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold text-green-600" data-testid="stat-approved-requests">
                      {stats.approved}
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                    <CardTitle className="text-sm font-medium">Rejected</CardTitle>
                    <XCircle className="h-4 w-4 text-red-600" />
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold text-red-600" data-testid="stat-rejected-requests">
                      {stats.rejected}
                    </div>
                  </CardContent>
                </Card>
              </div>

              {/* Leave Requests List */}
              <Card>
                <CardHeader>
                  <CardTitle>Your Leave Requests</CardTitle>
                  <CardDescription>
                    {leaveRequests.length === 0 
                      ? "You haven't submitted any leave requests yet." 
                      : `Showing ${leaveRequests.length} leave request${leaveRequests.length !== 1 ? 's' : ''}`}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  {leaveRequests.length === 0 ? (
                    <div className="text-center py-12">
                      <Calendar className="mx-auto h-12 w-12 text-muted-foreground" />
                      <h3 className="mt-2 text-sm font-semibold">No leave requests</h3>
                      <p className="mt-1 text-sm text-muted-foreground">
                        Get started by submitting your first leave request.
                      </p>
                      <div className="mt-6">
                        <Dialog>
                          <DialogTrigger asChild>
                            <Button data-testid="button-first-leave-request">
                              <Plus className="h-4 w-4 mr-2" />
                              Submit Leave Request
                            </Button>
                          </DialogTrigger>
                          <DialogContent className="max-w-md">
                            <DialogHeader>
                              <DialogTitle>Submit Leave Request</DialogTitle>
                            </DialogHeader>
                            <LeaveRequestForm
                              onSubmit={handleSubmitLeaveRequest}
                              onCancel={() => {}}
                              isLoading={submitLeaveRequestMutation.isPending}
                            />
                          </DialogContent>
                        </Dialog>
                      </div>
                    </div>
                  ) : (
                    <div className="space-y-4">
                      {leaveRequests.map((request) => (
                        <div key={request.id} className="border border-border rounded-lg p-6" data-testid={`card-request-${request.id}`}>
                          <div className="flex items-start justify-between mb-4">
                            <div>
                              <div className="flex items-center space-x-2 mb-2">
                                {getLeaveTypeBadge(request.leaveType)}
                                {getStatusBadge(request.status)}
                              </div>
                              <h3 className="text-lg font-semibold">
                                {request.leaveType.charAt(0).toUpperCase() + request.leaveType.slice(1)} Leave
                              </h3>
                              <p className="text-sm text-muted-foreground">
                                Submitted on {format(new Date(request.createdAt), "MMM dd, yyyy 'at' hh:mm a")}
                              </p>
                            </div>
                            <div className="text-right">
                              <p className="text-lg font-semibold">{request.daysRequested}</p>
                              <p className="text-sm text-muted-foreground">days</p>
                            </div>
                          </div>

                          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                            <div>
                              <p className="text-sm font-medium text-muted-foreground">Start Date</p>
                              <p className="text-sm">{format(new Date(request.startDate), "EEEE, MMM dd, yyyy")}</p>
                            </div>
                            <div>
                              <p className="text-sm font-medium text-muted-foreground">End Date</p>
                              <p className="text-sm">{format(new Date(request.endDate), "EEEE, MMM dd, yyyy")}</p>
                            </div>
                          </div>

                          {request.reason && (
                            <div className="mb-4">
                              <p className="text-sm font-medium text-muted-foreground mb-1">Reason</p>
                              <p className="text-sm bg-secondary p-3 rounded-md">{request.reason}</p>
                            </div>
                          )}

                          {request.status !== "pending" && (
                            <div className="pt-4 border-t border-border">
                              <div className="flex items-start justify-between">
                                <div>
                                  <p className="text-sm font-medium text-muted-foreground">
                                    {request.status === "approved" ? "Approved" : "Rejected"}
                                    {request.reviewedAt && ` on ${format(new Date(request.reviewedAt), "MMM dd, yyyy")}`}
                                  </p>
                                  {request.reviewComments && (
                                    <p className="text-sm mt-1 bg-secondary p-3 rounded-md">
                                      {request.reviewComments}
                                    </p>
                                  )}
                                </div>
                              </div>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </CardContent>
              </Card>

              {/* Leave Policy Information */}
              <Card className="mt-6">
                <CardHeader>
                  <CardTitle>Leave Policy</CardTitle>
                  <CardDescription>Important information about leave requests</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="flex items-start space-x-3">
                      <CheckCircle className="h-5 w-5 text-green-600 mt-0.5" />
                      <div>
                        <p className="font-medium">Annual Leave Allocation</p>
                        <p className="text-sm text-muted-foreground">You have {stats.balance} days remaining for this year</p>
                      </div>
                    </div>
                    <div className="flex items-start space-x-3">
                      <CheckCircle className="h-5 w-5 text-green-600 mt-0.5" />
                      <div>
                        <p className="font-medium">Advance Notice</p>
                        <p className="text-sm text-muted-foreground">Submit requests at least 2 weeks in advance for planned leave</p>
                      </div>
                    </div>
                    <div className="flex items-start space-x-3">
                      <CheckCircle className="h-5 w-5 text-green-600 mt-0.5" />
                      <div>
                        <p className="font-medium">Approval Process</p>
                        <p className="text-sm text-muted-foreground">Requests are reviewed by your supervisor and HR team</p>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/employee-profile.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { format } from "date-fns";
import { apiRequest } from "@/lib/queryClient";
import { getAuthHeaders } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import Sidebar from "@/components/sidebar";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";
import { User, Mail, Building, Calendar, Shield, Edit, Save, X } from "lucide-react";
import type { Employee } from "@shared/schema";

export default function EmployeeProfile() {
  const { toast } = useToast();
  const { user, updateUser } = useAuth();
  const queryClient = useQueryClient();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: user?.firstName || "",
    lastName: user?.lastName || "",
    email: user?.email || "",
    position: user?.position || "",
    department: user?.department || "",
  });

  // Fetch full user profile
  const { data: profile, isLoading } = useQuery<Employee>({
    queryKey: ["/api/auth/me"],
    queryFn: async () => {
      const response = await fetch("/api/auth/me", {
        headers: getAuthHeaders(),
      });
      if (!response.ok) throw new Error("Failed to fetch profile");
      return await response.json();
    },
    initialData: user,
  });

  // Update profile mutation
  const updateProfileMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("PUT", `/api/employees/${profile?.id}`, data);
      return await response.json();
    },
    onSuccess: (updatedProfile) => {
      toast({ 
        title: "Success", 
        description: "Profile updated successfully" 
      });
      setIsEditing(false);
      updateUser(updatedProfile);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update profile",
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    updateProfileMutation.mutate(formData);
  };

  const handleCancel = () => {
    setFormData({
      firstName: profile?.firstName || "",
      lastName: profile?.lastName || "",
      email: profile?.email || "",
      position: profile?.position || "",
      department: profile?.department || "",
    });
    setIsEditing(false);
  };

  const getInitials = (firstName: string, lastName: string) => {
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase();
  };

  const getRoleBadge = (role: string) => {
    return role === "admin" ? (
      <Badge className="bg-blue-100 text-blue-800 hover:bg-blue-100">
        <Shield className="h-3 w-3 mr-1" />
        Administrator
      </Badge>
    ) : (
      <Badge variant="secondary">
        <User className="h-3 w-3 mr-1" />
        Employee
      </Badge>
    );
  };

  if (isLoading) {
    return (
      <div className="flex h-screen bg-background">
        <Sidebar />
        <div className="flex-1 flex items-center justify-center">
          <div className="text-muted-foreground">Loading profile...</div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="flex h-screen bg-background">
        <Sidebar />
        <div className="flex-1 flex items-center justify-center">
          <div className="text-muted-foreground">Profile not found</div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-background">
      <Sidebar />

      <div className="flex-1 overflow-hidden">
        <div className="relative z-0 flex-1 overflow-y-auto focus:outline-none">
          <div className="py-6">
            <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
              {/* Header */}
              <div className="mb-8">
                <h1 className="text-2xl font-bold">My Profile</h1>
                <p className="text-muted-foreground">Manage your personal information and account settings.</p>
              </div>

              {/* Profile Header */}
              <Card className="mb-8">
                <CardContent className="pt-6">
                  <div className="flex items-center space-x-6">
                    <div className="h-20 w-20 bg-primary/10 rounded-full flex items-center justify-center">
                      <span className="text-2xl font-medium text-primary">
                        {getInitials(profile.firstName, profile.lastName)}
                      </span>
                    </div>
                    <div className="flex-1">
                      <div className="flex items-center justify-between">
                        <div>
                          <h2 className="text-2xl font-bold" data-testid="profile-name">
                            {profile.firstName} {profile.lastName}
                          </h2>
                          <p className="text-muted-foreground" data-testid="profile-position">
                            {profile.position} ‚Ä¢ {profile.department}
                          </p>
                          <div className="flex items-center space-x-2 mt-2">
                            {getRoleBadge(profile.role)}
                            <Badge className="bg-green-100 text-green-800 hover:bg-green-100">
                              Active
                            </Badge>
                          </div>
                        </div>
                        <Button
                          onClick={() => setIsEditing(!isEditing)}
                          variant={isEditing ? "outline" : "default"}
                          data-testid="button-edit-profile"
                        >
                          {isEditing ? (
                            <>
                              <X className="h-4 w-4 mr-2" />
                              Cancel
                            </>
                          ) : (
                            <>
                              <Edit className="h-4 w-4 mr-2" />
                              Edit Profile
                            </>
                          )}
                        </Button>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Tabs defaultValue="personal" className="space-y-6">
                <TabsList className="grid w-full grid-cols-3">
                  <TabsTrigger value="personal" data-testid="tab-personal">Personal Info</TabsTrigger>
                  <TabsTrigger value="employment" data-testid="tab-employment">Employment</TabsTrigger>
                  <TabsTrigger value="account" data-testid="tab-account">Account</TabsTrigger>
                </TabsList>

                {/* Personal Information */}
                <TabsContent value="personal" className="space-y-6">
                  <Card>
                    <CardHeader>
                      <CardTitle>Personal Information</CardTitle>
                      <CardDescription>Your basic personal details</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-6">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="space-y-2">
                          <Label htmlFor="firstName">First Name</Label>
                          {isEditing ? (
                            <Input
                              id="firstName"
                              value={formData.firstName}
                              onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
                              data-testid="input-first-name"
                            />
                          ) : (
                            <div className="px-3 py-2 bg-secondary rounded-md" data-testid="display-first-name">
                              {profile.firstName}
                            </div>
                          )}
                        </div>

                        <div className="space-y-2">
                          <Label htmlFor="lastName">Last Name</Label>
                          {isEditing ? (
                            <Input
                              id="lastName"
                              value={formData.lastName}
                              onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
                              data-testid="input-last-name"
                            />
                          ) : (
                            <div className="px-3 py-2 bg-secondary rounded-md" data-testid="display-last-name">
                              {profile.lastName}
                            </div>
                          )}
                        </div>

                        <div className="space-y-2">
                          <Label htmlFor="email">Email Address</Label>
                          {isEditing ? (
                            <Input
                              id="email"
                              type="email"
                              value={formData.email}
                              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                              data-testid="input-email"
                            />
                          ) : (
                            <div className="px-3 py-2 bg-secondary rounded-md flex items-center" data-testid="display-email">
                              <Mail className="h-4 w-4 mr-2 text-muted-foreground" />
                              {profile.email}
                            </div>
                          )}
                        </div>
                      </div>

                      {isEditing && (
                        <>
                          <Separator />
                          <div className="flex justify-end space-x-3">
                            <Button 
                              variant="outline" 
                              onClick={handleCancel}
                              data-testid="button-cancel-edit"
                            >
                              Cancel
                            </Button>
                            <Button 
                              onClick={handleSave}
                              disabled={updateProfileMutation.isPending}
                              data-testid="button-save-profile"
                            >
                              <Save className="h-4 w-4 mr-2" />
                              Save Changes
                            </Button>
                          </div>
                        </>
                      )}
                    </CardContent>
                  </Card>
                </TabsContent>

                {/* Employment Information */}
                <TabsContent value="employment" className="space-y-6">
                  <Card>
                    <CardHeader>
                      <CardTitle>Employment Details</CardTitle>
                      <CardDescription>Your role and department information</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-6">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="space-y-2">
                          <Label htmlFor="position">Position</Label>
                          {isEditing ? (
                            <Input
                              id="position"
                              value={formData.position}
                              onChange={(e) => setFormData({ ...formData, position: e.target.value })}
                              data-testid="input-position"
                            />
                          ) : (
                            <div className="px-3 py-2 bg-secondary rounded-md" data-testid="display-position">
                              {profile.position}
                            </div>
                          )}
                        </div>

                        <div className="space-y-2">
                          <Label htmlFor="department">Department</Label>
                          {isEditing ? (
                            <Input
                              id="department"
                              value={formData.department}
                              onChange={(e) => setFormData({ ...formData, department: e.target.value })}
                              data-testid="input-department"
                            />
                          ) : (
                            <div className="px-3 py-2 bg-secondary rounded-md flex items-center" data-testid="display-department">
                              <Building className="h-4 w-4 mr-2 text-muted-foreground" />
                              {profile.department}
                            </div>
                          )}
                        </div>

                        <div className="space-y-2">
                          <Label>Employee Role</Label>
                          <div className="px-3 py-2 bg-secondary rounded-md">
                            {getRoleBadge(profile.role)}
                          </div>
                        </div>

                        <div className="space-y-2">
                          <Label>Leave Balance</Label>
                          <div className="px-3 py-2 bg-secondary rounded-md" data-testid="display-leave-balance">
                            {profile.leaveBalance} days remaining
                          </div>
                        </div>

                        <div className="space-y-2">
                          <Label>Join Date</Label>
                          <div className="px-3 py-2 bg-secondary rounded-md flex items-center" data-testid="display-join-date">
                            <Calendar className="h-4 w-4 mr-2 text-muted-foreground" />
                            {format(new Date(profile.createdAt), "MMMM dd, yyyy")}
                          </div>
                        </div>
                      </div>

                      {isEditing && (
                        <>
                          <Separator />
                          <div className="flex justify-end space-x-3">
                            <Button 
                              variant="outline" 
                              onClick={handleCancel}
                              data-testid="button-cancel-employment"
                            >
                              Cancel
                            </Button>
                            <Button 
                              onClick={handleSave}
                              disabled={updateProfileMutation.isPending}
                              data-testid="button-save-employment"
                            >
                              <Save className="h-4 w-4 mr-2" />
                              Save Changes
                            </Button>
                          </div>
                        </>
                      )}
                    </CardContent>
                  </Card>
                </TabsContent>

                {/* Account Settings */}
                <TabsContent value="account" className="space-y-6">
                  <Card>
                    <CardHeader>
                      <CardTitle>Account Settings</CardTitle>
                      <CardDescription>Manage your account preferences and security</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-6">
                      <div className="space-y-4">
                        <div>
                          <Label className="text-base font-medium">Account Status</Label>
                          <div className="mt-1">
                            <Badge className="bg-green-100 text-green-800 hover:bg-green-100">
                              Active Account
                            </Badge>
                          </div>
                          <p className="text-sm text-muted-foreground mt-1">
                            Your account is active and in good standing
                          </p>
                        </div>

                        <Separator />

                        <div>
                          <Label className="text-base font-medium">Password</Label>
                          <p className="text-sm text-muted-foreground mt-1">
                            Last updated: Never
                          </p>
                          <Button variant="outline" size="sm" className="mt-2" disabled>
                            Change Password
                          </Button>
                        </div>

                        <Separator />

                        <div>
                          <Label className="text-base font-medium">Data Export</Label>
                          <p className="text-sm text-muted-foreground mt-1">
                            Download a copy of your personal data
                          </p>
                          <Button variant="outline" size="sm" className="mt-2" disabled>
                            Request Data Export
                          </Button>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                </TabsContent>
              </Tabs>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
